    fn test_base64_decode() {
        assert_eq!("Man is distinguished, not only by his reason, but by this singular passion from other animals, which is a lust of the mind, that by a perseverance of delight in the continued and indefatigable generation of knowledge, exceeds the short vehemence of any carnal pleasure.".as_bytes().to_vec(), base64_decode("".to_string()))
    }
